import React, { useEffect, useState } from 'react';
import useIsInViewport from 'use-is-in-viewport';
import IEducationItem from '../../typescript/Interfaces/IEducationItem';
import { educationList } from '../../data/education';
import EducationItem from '../EducationItem';
import Section from '../Section';
import Headline from '../Headline';

const Education = () => {
  const [isInViewport, targetRef] = useIsInViewport();
  const [animate, setAnimate] = useState<boolean>(false);

  useEffect(() => {
    if (isInViewport) {
      setAnimate(true);
    }
  }, [isInViewport]);

  return (
    <div ref={targetRef}>
      <Headline headline="Education" white noPadding />
      <Section tall white noPadding>
        {educationList.map((item: IEducationItem, idx: number) => (
          <EducationItem item={item} animate={animate} animateDelay={idx * 4} />
        ))}
      </Section>
    </div>
  );
};

export default Education;
