import React, { useEffect, useState } from 'react';
import classNames from 'classnames';
import useIsInViewport from 'use-is-in-viewport';
import styles from './TimelineItem.module.scss';

const {
  container,
  rightSide,
  content,
  year: yearStyle,
  dividor,
  animateUp,
  animateFromLeft,
  animateFromRight,
} = styles;
interface ITimelineItemProps {
  children: React.ReactNode;
  year: string;
}

const TimelineItem = ({ year, children }: ITimelineItemProps) => {
  const [isInViewport, targetRef] = useIsInViewport();
  const [animate, setAnimate] = useState<boolean>(false);

  useEffect(() => {
    if (isInViewport) {
      setAnimate(true);
    }
  }, [isInViewport]);

  return (
    <div className={container} ref={targetRef}>
      <div className={classNames(yearStyle, { [animateFromLeft]: animate })}>
        {year}
      </div>
      <div className={classNames(dividor, { [animateUp]: animate })} />
      <div className={classNames(rightSide, { [animateFromRight]: animate })}>
        <div className={content}>{children}</div>
      </div>
    </div>
  );
};

export default TimelineItem;
