import React, { useEffect, useState } from 'react';
import classNames from 'classnames';
import useIsInViewport from 'use-is-in-viewport';
import styles from './TimelineItem.module.scss';

const {
  container,
  rightSide,
  content,
  year: yearStyle,
  dividor,
  animateUp: animateUpStyle,
} = styles;
interface ITimelineItemProps {
  children: React.ReactNode;
  year: string;
}

const TimelineItem = ({ year, children }: ITimelineItemProps) => {
  const [isInViewport, targetRef] = useIsInViewport();
  const [animateUp, setAnimateUp] = useState<boolean>(false);

  useEffect(() => {
    if (isInViewport) {
      setAnimateUp(true);
    }
  }, [isInViewport]);

  console.log('animateUp', animateUp);

  return (
    <div className={container} ref={targetRef}>
      <div className={yearStyle}>{year}</div>
      <div className={classNames(dividor, { [animateUpStyle]: animateUp })} />
      <div className={rightSide}>
        <div className={content}>{children}</div>
      </div>
    </div>
  );
};

export default TimelineItem;
