import React, { useState, useEffect } from 'react';
import classNames from 'classnames';
import SwipeableViews from 'react-swipeable-views';
import IPassion from '../../typescript/Interfaces/IPassion';
import { passionList, passionImages } from '../../data/about';
import styles from './TextSwiper.module.scss';

const {
  container,
  flexWrapper,
  item,
  image,
  rightSide,
  titleWrapper,
  separator,
  passionTitle,
  paragraph: paragraphStyle,
  arrowLeft,
  arrowRight,
  fadeInAnimation,
} = styles;

const TextSwiper = () => {
  const [index, setIndex] = useState<number>(0);
  const [fadeIn, setFadeIn] = useState<boolean>(false);
  const isLeftDisabled = index < 1;
  const isRightDisabled = index + 1 === passionList.length;

  useEffect(() => {
    setFadeIn(true);
  }, [index]);

  useEffect(() => {
    if (fadeIn) {
      setTimeout(() => {
        setFadeIn(false);
      }, 800);
    }
  }, [fadeIn]);

  return (
    <div className={container}>
      <div className={flexWrapper}>
        <div
          className={classNames(image, { [fadeInAnimation]: fadeIn })}
          style={{ backgroundImage: `url(${passionImages[index]})` }}
        />
        <div className={rightSide}>
          <h2>Career and passion</h2>
          <div className={separator}></div>
          <div className={titleWrapper}>
            <div
              role="button"
              className={arrowLeft}
              onClick={isLeftDisabled ? () => {} : () => setIndex(index - 1)}
            />
            <h3
              className={classNames(passionTitle, {
                [fadeInAnimation]: fadeIn,
              })}
            >
              {passionList[index]?.title}
            </h3>
            <div
              role="button"
              className={arrowRight}
              onClick={isRightDisabled ? () => {} : () => setIndex(index + 1)}
            />
          </div>
          <SwipeableViews
            index={index}
            onSwitching={(idx: number) => {
              setIndex(idx);
            }}
            enableMouseEvents
          >
            {passionList.map(({ title, paragraph }: IPassion) => (
              <div className={item}>
                <p className={paragraphStyle}>{paragraph}</p>
              </div>
            ))}
          </SwipeableViews>
        </div>
      </div>
    </div>
  );
};

export default TextSwiper;
