import React, { useEffect, useState } from 'react';
import useIsInViewport from 'use-is-in-viewport';
import classNames from 'classnames';
import Section from '../Section';
import ProgressBar from '../ProgressBar';
import Headline from '../Headline';
import styles from './Skills.module.scss';

const {
  subtitle: subtitleStyle,
  h3,
  flexWrapper,
  progressBars,
  bullet,
  advancedBullet,
  intermediateBullet,
  fundamentalBullet,
} = styles;

const Skills = () => {
  const [isInViewport, targetRef] = useIsInViewport();
  const [animate, setAnimate] = useState<boolean>(false);

  useEffect(() => {
    if (isInViewport) {
      setAnimate(true);
    }
  }, [isInViewport]);

  const subtitle = (
    <>
      <div className={subtitleStyle}>
        <div className={classNames(bullet, advancedBullet)} />
        <span>Advanced</span>
      </div>
      <div className={subtitleStyle}>
        <div className={classNames(bullet, intermediateBullet)} />
        <span>Intermetiate</span>
      </div>
      <div className={subtitleStyle}>
        <div className={classNames(bullet, fundamentalBullet)} />
        <span>Fundamental</span>
      </div>
    </>
  );

  return (
    <>
      <Headline headline="Computer Skills" subtitle={subtitle} noPadding />
      <Section tall>
        <div className={flexWrapper}>
          <div className={progressBars}>
            <h3 className={h3}>Programming Languages</h3>
            <ProgressBar title="HTML" width={90} advanced />
            <ProgressBar
              title="CSS, SCSS, LESS, CSS Modules"
              width={90}
              advanced
            />
            <ProgressBar title="JavaScript" width={90} advanced />
            <ProgressBar title="Typescript" width={80} advanced />
            <ProgressBar title="Java" width={75} intermediate />
            <ProgressBar title="Python" width={60} fundamental />
            <ProgressBar title="C/C++" width={50} fundamental />
            <ProgressBar title="SQL/PostgreSQL" width={50} fundamental />
          </div>
          <div className={progressBars}>
            <h3 className={h3}>Frameworks / Software</h3>
            <ProgressBar title="React.js with Redux" width={90} advanced />
            <ProgressBar title="Next.js" width={80} advanced />
            <ProgressBar title="React Native" width={75} intermediate />
            <ProgressBar title="Express" width={75} intermediate />
            <ProgressBar
              title="Google Analytics and Tag Manager"
              width={75}
              intermediate
            />
            <ProgressBar title="Bugsnag" width={70} fundamental />
            <ProgressBar title="Contentful" width={70} fundamental />
            <ProgressBar title="Cypress" width={60} fundamental />
          </div>
        </div>
      </Section>
    </>
  );
};

export default Skills;
