import React, { useState } from 'react';
import SwipeableViews from 'react-swipeable-views';
import IPassion from '../../typescript/Interfaces/IPassion';
import { passionList } from '../../data/about';
import styles from './TextSwiper.module.scss';

const { container, item, arrowLeft, arrowRight } = styles;

const TextSwiper = () => {
  const [index, setIndex] = useState<number>(0);

  return (
    <div className={container}>
      <div className={arrowLeft} />
      <SwipeableViews enableMouseEvents>
        {passionList.map(({ title, paragraph }: IPassion) => (
          <div className={item}>
            <h3>{title}</h3>
            <p>{paragraph}</p>
          </div>
        ))}
      </SwipeableViews>
      <div className={arrowRight} />
    </div>
  );
};

export default TextSwiper;
